// src/components/parent/sections/ParentInfo.jsx
import React, { useState, useEffect } from 'react';
import { api } from '../../../api';
import EditParentModal    from '../../modals/EditParentModal';
import DeleteAccountModal from '../../modals/DeleteAccountModal';

const ParentInfo = () => {
  const [data, setData]             = useState(null);
  const [showEdit, setShowEdit]     = useState(false);
  const [showDelete, setShowDelete] = useState(false);

  useEffect(() => {
    api.get('/auth/me')
      .then(res => setData(res.data))
      .catch(console.error);
  }, []);

  const handleSave = updated =>
    api.put('/auth/me', updated)
      .then(res => {
        setData(res.data);
        setShowEdit(false);
      })
      .catch(console.error);

  const handleDelete = () =>
    api.delete('/auth/me')
      .then(() => {
        localStorage.removeItem('token');
        window.location.reload();
      })
      .catch(console.error);

  if (!data) return <div>Cargando…</div>;

  return (
    <div className="info-section">
      <h1>Información del padre</h1>
      <div className="info-card">
        <div className="info-row">
          <span className="info-label">Nombre:</span>
          <span className="info-value">{data.name}</span>
        </div>
        <div className="info-row">
          <span className="info-label">Teléfono:</span>
          <span className="info-value">{data.phone}</span>
        </div>
        <div className="info-row">
          <span className="info-label">Correo:</span>
          <span className="info-value">{data.email}</span>
        </div>
        <div className="action-buttons">
          <button className="btn-edit" onClick={() => setShowEdit(true)}>
            <i className="fas fa-edit"></i> Editar
          </button>
          <button className="btn-delete" onClick={() => setShowDelete(true)}>
            <i className="fas fa-trash-alt"></i> Eliminar cuenta
          </button>
        </div>
      </div>

      <EditParentModal
        show={showEdit}
        parentData={data}
        onSave={handleSave}
        onHide={() => setShowEdit(false)}
      />
      <DeleteAccountModal
        show={showDelete}
        onConfirm={handleDelete}
        onHide={() => setShowDelete(false)}
      />
    </div>
  );
};

export default ParentInfo;
