// src/components/finance/sections/PaymentReview.jsx
import React, { useState, useEffect } from 'react';
import { Table, Button, Badge, Form } from 'react-bootstrap';
import ReceiptDetails      from './ReceiptDetails';
import ReceiptCommentModal from '../modals/ReceiptCommentModal';

const monthNames = [
  { label: 'Enero',     value: '01' },
  { label: 'Febrero',   value: '02' },
  { label: 'Marzo',     value: '03' },
  { label: 'Abril',     value: '04' },
  { label: 'Mayo',      value: '05' },
  { label: 'Junio',     value: '06' },
  { label: 'Julio',     value: '07' },
  { label: 'Agosto',    value: '08' },
  { label: 'Septiembre',value: '09' },
  { label: 'Octubre',   value: '10' },
  { label: 'Noviembre', value: '11' },
  { label: 'Diciembre', value: '12' }
];

const PaymentReview = () => {
  // Padres ficticios
  const [parents] = useState([
    { id: 1, name: 'Juan Pérez',    phone: '555-1234' },
    { id: 2, name: 'Ana López',     phone: '555-5678' },
    { id: 3, name: 'Luisa García',  phone: ''        }
  ]);

  // Comprobantes ficticios
  const [receipts] = useState([
    { id: 1, parentId: 1, studentName: 'María Pérez',  amount: 150.00, date: '2023-05-15', status: 'pending',  comment: '' },
    { id: 2, parentId: 2, studentName: 'Carlos López', amount: 150.00, date: '2023-05-10', status: 'approved', comment: 'Pago completo' },
    { id: 3, parentId: 3, studentName: 'Pedro García', amount: 120.00, date: '2024-06-05', status: 'rejected', comment: 'Monto incorrecto' }
  ]);

  // Estado de filtro: año y mes
  const now = new Date();
  const [filterYear, setFilterYear]   = useState(String(now.getFullYear()));
  const [filterMonth, setFilterMonth] = useState(String(now.getMonth() + 1).padStart(2, '0'));

  const [selectedReceipt, setSelectedReceipt]   = useState(null);
  const [showDetails, setShowDetails]           = useState(false);
  const [showCommentModal, setShowCommentModal] = useState(false);

  // Extraer lista de años únicos de los receipts
  const years = Array.from(
    new Set(receipts.map(r => new Date(r.date).getFullYear()))
  ).sort((a,b) => b - a);

  // Filtrar comprobantes
  const filtered = receipts.filter(r => {
    const dt = new Date(r.date);
    const m  = String(dt.getMonth() + 1).padStart(2, '0');
    const y  = String(dt.getFullYear());
    return y === filterYear && m === filterMonth;
  });

  // Construir filas por cada padre
  const rows = parents.flatMap(p => {
    const recs = filtered.filter(r => r.parentId === p.id);
    if (recs.length > 0) {
      return recs.map(r => ({ parent: p, receipt: r }));
    } else {
      return [{ parent: p, receipt: null }];
    }
  });

  return (
    <div className="payment-review">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Revisión de Comprobantes</h2>
        <div className="d-flex gap-2">
          <Form.Select
            style={{ width: 120 }}
            value={filterYear}
            onChange={e => setFilterYear(e.target.value)}
          >
            {years.map(y => (
              <option key={y} value={String(y)}>{y}</option>
            ))}
          </Form.Select>
          <Form.Select
            style={{ width: 140 }}
            value={filterMonth}
            onChange={e => setFilterMonth(e.target.value)}
          >
            {monthNames.map(m => (
              <option key={m.value} value={m.value}>
                {m.label}
              </option>
            ))}
          </Form.Select>
        </div>
      </div>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Padre/Madre</th>
            <th>Teléfono</th>
            <th>Estudiante</th>
            <th>Monto</th>
            <th>Fecha</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, idx) => {
            const { parent, receipt } = row;
            return (
              <tr key={`${parent.id}-${idx}`}>
                <td>{receipt?.id ?? '-'}</td>
                <td>{parent.name}</td>
                <td>{parent.phone || '—'}</td>
                <td>{receipt?.studentName ?? '—'}</td>
                <td>{receipt ? `$${receipt.amount.toFixed(2)}` : '—'}</td>
                <td>{receipt ? new Date(receipt.date).toISOString().split('T')[0] : '—'}</td>
                <td>
                  {receipt ? (
                    <Badge bg={
                      receipt.status === 'approved' ? 'success' :
                      receipt.status === 'rejected' ? 'danger' :
                      'warning'
                    }>
                      {receipt.status === 'approved'
                        ? 'Aprobado'
                        : receipt.status === 'rejected'
                        ? 'Rechazado'
                        : 'Pendiente'
                      }
                    </Badge>
                  ) : (
                    <Badge bg="secondary">No hay</Badge>
                  )}
                </td>
                <td>
                  {receipt ? (
                    <div className="d-flex gap-2">
                      <Button
                        variant="info"
                        size="sm"
                        onClick={() => {
                          setSelectedReceipt(receipt);
                          setShowDetails(true);
                        }}
                      >
                        Ver
                      </Button>
                      <Button variant="success" size="sm" disabled>
                        Aprobar
                      </Button>
                      <Button variant="danger" size="sm" disabled>
                        Rechazar
                      </Button>
                    </div>
                  ) : (
                    <Button variant="outline-primary" size="sm">
                      Notificar por correo
                    </Button>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>

      <ReceiptDetails
        show={showDetails}
        onHide={() => setShowDetails(false)}
        receipt={selectedReceipt}
      />

      <ReceiptCommentModal
        show={showCommentModal}
        onHide={() => setShowCommentModal(false)}
        receipt={selectedReceipt}
        onSave={comment => {
          setShowCommentModal(false);
        }}
      />
    </div>
  );
};

export default PaymentReview;
