// src/components/Auth/AuthContainer.jsx
import React, { useState } from 'react';
import Login from './Login';
import Register from './Register';
import ForgotPassword from './ForgotPassword';
import ResetPassword from './ResetPassword';

const AuthContainer = ({ onLogin, resetMode = false }) => {
  const [activeForm, setActiveForm] = useState(resetMode ? 'reset' : 'login');

  return (
    <div className="auth-container">
      {activeForm === 'login' && (
        <Login
          onLogin={onLogin}
          onShowRegister={() => setActiveForm('register')}
          onShowForgotPassword={() => setActiveForm('forgot')}
        />
      )}

      {activeForm === 'register' && (
        <Register onBackToLogin={() => setActiveForm('login')} />
      )}

      {activeForm === 'forgot' && (
        <ForgotPassword onBackToLogin={() => setActiveForm('login')} />
      )}

      {activeForm === 'reset' && (
        <ResetPassword onBackToLogin={() => setActiveForm('login')} />
      )}
    </div>
  );
};

export default AuthContainer;
