import React, { useState } from 'react';
import Login           from './Login';
import Register        from './Register';
import ForgotPassword  from './ForgotPassword';
import ResetPassword   from './ResetPassword';

const AuthContainer = ({ onLogin, resetMode = false }) => {
  // form puede ser 'login' | 'register' | 'forgot' | 'reset'
  const [form, setForm] = useState(resetMode ? 'reset' : 'login');

  return (
    <div className="auth-container">
      {form === 'login' && (
        <Login
          onLogin={onLogin}
          onShowRegister={() => setForm('register')}
          onShowForgotPassword={() => setForm('forgot')}
        />
      )}
      {form === 'register' && (
        <Register onBackToLogin={() => setForm('login')} />
      )}
      {form === 'forgot' && (
        <ForgotPassword onBackToLogin={() => setForm('login')} />
      )}
      {form === 'reset' && (
        <ResetPassword onBackToLogin={() => setForm('login')} />
      )}
    </div>
  );
};

export default AuthContainer;
