// src/components/finance/sections/PaymentReview.jsx
import React, { useState, useEffect } from 'react';
import { Table, Button, Badge, Form } from 'react-bootstrap';
import ReceiptDetails       from './ReceiptDetails';
import ReceiptCommentModal  from '../modals/ReceiptCommentModal';
import { format, parseISO } from 'date-fns';

const monthNames = [
  { label: 'Enero',   value: '01' },
  { label: 'Febrero', value: '02' },
  { label: 'Marzo',   value: '03' },
  { label: 'Abril',   value: '04' },
  { label: 'Mayo',    value: '05' },
  { label: 'Junio',   value: '06' },
  { label: 'Julio',   value: '07' },
  { label: 'Agosto',  value: '08' },
  { label: 'Septiembre', value: '09' },
  { label: 'Octubre', value: '10' },
  { label: 'Noviembre',value: '11' },
  { label: 'Diciembre',value: '12' }
];

const PaymentReview = () => {
  // Mock parents list (replace with API call)
  const [parents, setParents] = useState([
    { id: 1, name: 'Juan Pérez',  phone: '555-1234' },
    { id: 2, name: 'Ana López',   phone: '555-5678' },
    { id: 3, name: 'Luisa García',phone: ''        }
  ]);

  // Mock receipts (replace with API call)
  const [receipts, setReceipts] = useState([
    {
      id: 1,
      parentId: 1,
      studentName: 'María Pérez',
      amount: 150.00,
      date: '2023-05-15',
      imageUrl: '/receipts/1.jpg',
      status: 'pending',
      comment: ''
    },
    {
      id: 2,
      parentId: 2,
      studentName: 'Carlos López',
      amount: 150.00,
      date: '2023-05-10',
      imageUrl: '/receipts/2.jpg',
      status: 'approved',
      comment: 'Pago completo'
    },
    {
      id: 3,
      parentId: 3,
      studentName: 'Pedro García',
      amount: 120.00,
      date: '2023-06-05',
      imageUrl: '/receipts/3.jpg',
      status: 'rejected',
      comment: 'Monto incorrecto'
    }
  ]);

  // Selected filter month (format MM-YYYY)
  const [filterMonth, setFilterMonth] = useState(
    `${String(new Date().getMonth() + 1).padStart(2, '0')}-${new Date().getFullYear()}`
  );

  const [selectedReceipt, setSelectedReceipt] = useState(null);
  const [showDetails, setShowDetails]     = useState(false);
  const [showCommentModal, setShowCommentModal] = useState(false);

  // Receipts filtered by month
  const filtered = receipts.filter(r => {
    const dt = parseISO(r.date);
    const m  = String(dt.getMonth() + 1).padStart(2, '0');
    const y  = dt.getFullYear();
    return `${m}-${y}` === filterMonth;
  });

  // Build a row for each parent
  const rows = parents.map(p => {
    const recs = filtered.filter(r => r.parentId === p.id);
    if (recs.length > 0) {
      return recs.map(r => ({
        parent: p,
        receipt: r
      }));
    } else {
      return [{
        parent: p,
        receipt: null
      }];
    }
  }).flat();

  return (
    <div className="payment-review">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Revisión de Comprobantes</h2>
        <Form.Select
          style={{ width: '200px' }}
          value={filterMonth}
          onChange={e => setFilterMonth(e.target.value)}
        >
          {/* Listamos meses con año actual y anteriores (aquí solo año actual) */}
          {monthNames.map(m => (
            <option
              key={m.value}
              value={`${m.value}-${new Date().getFullYear()}`}
            >
              {m.label} {new Date().getFullYear()}
            </option>
          ))}
        </Form.Select>
      </div>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Padre/Madre</th>
            <th>Teléfono</th>
            <th>Estudiante</th>
            <th>Monto</th>
            <th>Fecha</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, idx) => {
            const { parent, receipt } = row;
            return (
              <tr key={parent.id + '-' + idx}>
                <td>{receipt?.id ?? '-'}</td>
                <td>{parent.name}</td>
                <td>{parent.phone || '—'}</td>
                <td>{receipt?.studentName ?? '—'}</td>
                <td>
                  {receipt
                    ? `$${receipt.amount.toFixed(2)}`
                    : '—'
                  }
                </td>
                <td>
                  {receipt
                    ? format(parseISO(receipt.date), 'yyyy-MM-dd')
                    : '—'
                  }
                </td>
                <td>
                  {receipt ? (
                    <Badge
                      bg={
                        receipt.status === 'approved' ? 'success' :
                        receipt.status === 'rejected' ? 'danger' :
                        'warning'
                      }
                    >
                      {receipt.status === 'approved' ? 'Aprobado' :
                       receipt.status === 'rejected' ? 'Rechazado' :
                       'Pendiente'}
                    </Badge>
                  ) : (
                    <Badge bg="secondary">No hay</Badge>
                  )}
                </td>
                <td>
                  {receipt ? (
                    <div className="d-flex gap-2">
                      <Button
                        variant="info"
                        size="sm"
                        onClick={() => {
                          setSelectedReceipt(receipt);
                          setShowDetails(true);
                        }}
                      >
                        Ver
                      </Button>
                      <Button
                        variant="success"
                        size="sm"
                        disabled={receipt.status === 'approved'}
                        onClick={() => {
                          // aprobar...
                        }}
                      >
                        Aprobar
                      </Button>
                      <Button
                        variant="danger"
                        size="sm"
                        disabled={receipt.status === 'rejected'}
                        onClick={() => {
                          setSelectedReceipt(receipt);
                          setShowCommentModal(true);
                        }}
                      >
                        Rechazar
                      </Button>
                    </div>
                  ) : (
                    <Button
                      variant="outline-primary"
                      size="sm"
                      onClick={() => {
                        // acción futura: notificar por correo
                      }}
                    >
                      Notificar por correo
                    </Button>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>

      <ReceiptDetails
        show={showDetails}
        onHide={() => setShowDetails(false)}
        receipt={selectedReceipt}
      />

      <ReceiptCommentModal
        show={showCommentModal}
        onHide={() => setShowCommentModal(false)}
        receipt={selectedReceipt}
        onSave={comment => {
          // ... manejador de rechazo con comentario
          setShowCommentModal(false);
        }}
      />
    </div>
  );
};

export default PaymentReview;
