// src/components/modals/EditStudentModal.jsx
import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const EditStudentModal = ({ show, onHide, student, onSave }) => {
  const [formData, setFormData] = useState({
    name: '',
    grade: '',
    address: ''
  });

  // Al abrir el modal, carga los datos del estudiante
  useEffect(() => {
    if (student) {
      setFormData({
        name:    student.name    || '',
        grade:   student.grade   || '',
        address: student.address || ''
      });
    }
  }, [student]);

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    onSave({ _id: student._id, ...formData });
    onHide();
  };

  return (
    <Modal show={show} onHide={onHide} centered>
      <Modal.Header closeButton>
        <Modal.Title>Editar estudiante</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Nombre completo</Form.Label>
            <Form.Control
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Nombre del estudiante"
              required
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Grado académico</Form.Label>
            <Form.Control
              type="text"
              name="grade"
              value={formData.grade}
              onChange={handleChange}
              placeholder="Ej: 5to Grado"
              required
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Dirección</Form.Label>
            <Form.Control
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="Dirección del estudiante"
              required
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={handleSubmit}>
          Guardar cambios
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditStudentModal;
