import React, { useState, useEffect } from 'react';
import { api } from '../../../api';
import AddStudentModal  from '../../modals/AddStudentModal';
import EditStudentModal from '../../modals/EditStudentModal';
import StudentCard      from '../StudentCard';   // ← UN nivel arriba

const StudentInfo = () => {
  const [students, setStudents] = useState([]);
  const [showAdd, setShowAdd]   = useState(false);
  const [showEdit, setShowEdit] = useState(false);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    api.get('/students').then(({ data }) => setStudents(data));
  }, []);

  const handleAdd = async (newStudent) => {
    const { data } = await api.post('/students', newStudent);
    setStudents(prev => [data, ...prev]);       // ← inserta al inicio
    setShowAdd(false);
  };

  const handleEdit = async (upd) => {
    const { data } = await api.put(`/students/${upd._id}`, upd);
    setStudents(prev => prev.map(s => s._id === data._id ? data : s));
    setShowEdit(false);
  };

  const handleDelete = async (id) => {
    await api.delete(`/students/${id}`);
    setStudents(prev => prev.filter(s => s._id !== id));
  };

  return (
    <div className="info-section">
      <div className="section-header">
        <h1>Información de los estudiantes</h1>
        <button className="btn-add" onClick={() => setShowAdd(true)}>
          <i className="fas fa-plus"></i> Agregar estudiante
        </button>
      </div>
      <div className="students-grid">
        {students.map(st => (
          <StudentCard
            key={st._id}
            student={st}
            onEdit={() => { setSelected(st); setShowEdit(true); }}
            onDelete={() => handleDelete(st._id)}
          />
        ))}
      </div>
      <AddStudentModal
        show={showAdd}
        onHide={() => setShowAdd(false)}
        onSave={handleAdd}
      />
      <EditStudentModal
        show={showEdit}
        onHide={() => setShowEdit(false)}
        student={selected}
        onSave={handleEdit}
      />
    </div>
  );
};

export default StudentInfo;
